import React, { useEffect, useState, useContext } from "react";
import firebase from "firebase";
import "firebase/firestore";
import { useParams } from "react-router";
import AuthContext from "./../components/AuthContext";

export default function CoursesDetails(props) {
  const authContext = useContext(AuthContext);
  const params = useParams();
  function EnrollUser() {
    firebase
      .firestore()
      .collection("courses")
      .doc(params.data)
      .update({
        enrolleduser: firebase.firestore.FieldValue.arrayUnion(
          authContext.userDetails.email
        ),
      });
  }
  const [courseData, setCourseData] = useState();
  useEffect(async () => {
    console.log(params.data, "data");
    firebase
      .firestore()
      .collection("courses")
      .doc(params.data)
      .get()
      .then((res) => {
        setCourseData(res.data());
        console.log(res.data());
      });
  }, []);
  return (
    <>
      <Headers pageName="Course Daetial" />
      {courseData && (
        <>
          <div className="container" style={{ maxWidth: "90%" }}>
            <div
              className="d-flex flex-row mt-5"
              style={{ justifyContent: "space-between" }}
            >
              <div style={{ width: "25%" }}>
                <img
                  src={courseData.courseimage}
                  alt="courseimages"
                  style={{
                    maxWidth: "100%",
                    height: 250,
                    objectFit: "cover",
                  }}
                />
              </div>
              <div style={{ width: "45%" }}>
                <h4
                  className="font-weight-bold text-danger"
                  style={{ maxWidth: "60%" }}
                >
                  {courseData.coursetitle}
                </h4>
                <small>By Abdullah Riaz</small>
                <p className="mt-1">{courseData.coursedescription}</p>
                <small className="font-weight-bold">(8 Reviews)</small>
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  width: "25%",
                  alignItems: "center",
                }}
              >
                <button
                  className="btn btn-success btn-lg rounded-pill"
                  onClick={() => EnrollUser()}
                >
                  Enroll Now
                </button>
              </div>
            </div>
          </div>
          <div className="container" style={{ maxWidth: "90%" }}>
            <div
              className="d-flex flex-row mt-5"
              style={{ justifyContent: "space-between" }}
            >
              <div style={{ width: "60%" }}>
                <h4 className="font-weight-bold">About This Course</h4>
                <p className="mt-1">{courseData.coursedescription}</p>
                <h4 className="font-weight-bold">What You'll Learn</h4>
                <p className="mt-1">{courseData.coursedescription}</p>
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  width: "25%",
                  alignItems: "center",
                }}
              >
                <small className="font-weight-bold">
                  {" "}
                  Students: {courseData.enrolleduser.length}
                </small>
                <small className="font-weight-bold">
                  {" "}
                  {courseData.courseduration}
                </small>
                <button className="btn btn-success btn-lg rounded-pill">
                  Enroll Now
                </button>
              </div>
            </div>
          </div>
        </>
      )}
    </>
  );
}
